// This function takes msg.payload as a string of Process Inputs 
if (global.get("BSP_Port") !== undefined){
    var aux = (msg.payload[global.get("BSP_Port")].ProcessInputs).replace(/\s/g,""); //remove whitespace

    var BSP = {payload: parseInt(aux.slice(0,4),16).toString(2).padStart(16,'0')}; //leave the first 4 characters, convert to number

    var pressure = BSP.payload.slice(0,14);  //extract pressure and multiply by 0,01 to get result in bar

    var switchpoints = BSP.payload.slice(14,16);  //extract switchpoints
    var BSP_Pressure = {payload: parseInt(pressure,2)*0.01}

    if (BSP_Pressure.payload > 163) {  //eliminate the negative pressure
        BSP_Pressure = {payload: 0};
    }

    var BSP_SP2 = {payload: parseInt(switchpoints.split(' ')[0].substr(0,1),2)};  //separate and convert switchpoints to number
    var BSP_SP1 = {payload: parseInt(switchpoints.split(' ')[0].substr(1,1),2)};    

    var BSP00Y4_InfluxData = {payload:  //Package of BSP00Y4 data to send to InfluxDB
    [
    {
        measurement: "BSP00Y4_Pressure",
        fields: {
            Pressure: BSP_Pressure.payload
        },
        tags:{
           Variable:"Pressure"
        },
    },
    {
        measurement: "BSP00Y4_SwitchPoints",
        fields: {
            SP1: BSP_SP1.payload,
            SP2: BSP_SP2.payload
        },
        tags:{
            Variable:"SP's"
        },
    }
    ]
    }; 

}

return [BSP_Pressure, BSP_SP1, BSP_SP2, BSP00Y4_InfluxData];
